package com.example.taller2.Fragmentimport android.content.Contextimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.Buttonimport android.widget.TextViewimport androidx.fragment.app.Fragmentimport com.example.taller2.Activity.MainActivityimport com.example.taller2.Rclass PerfilFragment : Fragment() {    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        return inflater.inflate(R.layout.fragment_perfil, container, false)    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        val tvNombreApellido = view.findViewById<TextView>(R.id.nombre_apellido)        val tvCorreo = view.findViewById<TextView>(R.id.email)        val tvNumero = view.findViewById<TextView>(R.id.numerocel)        val btnEditarPerfil = view.findViewById<Button>(R.id.editarperfil)        // Recupera la sesión del usuario        val correoActivo = obtenerCorreoSesion()        if (correoActivo != null) {            val sharedPreferences = requireContext().getSharedPreferences("Usuarios", Context.MODE_PRIVATE)            val nombre = sharedPreferences.getString("$correoActivo-nombre", "No disponible")!!            val apellido = sharedPreferences.getString("$correoActivo-apellido", "No disponible")!!            val numero = sharedPreferences.getString("$correoActivo-numero", "No disponible")!!            tvNombreApellido.text = "$nombre $apellido"            tvCorreo.text = correoActivo            tvNumero.text = numero        }        // Abre EditarFragment desde el botón        btnEditarPerfil.setOnClickListener {            (requireActivity() as MainActivity).abrirEditarFragment()        }    }    /**     * Obtiene el correo de sesión activa desde SharedPreferences     */    private fun obtenerCorreoSesion(): String? {        val sharedPreferences = requireContext().getSharedPreferences("Sesion", Context.MODE_PRIVATE)        return sharedPreferences.getString("correo_activo", null)    }}